import './App.css';
import Login from './Login';
import { getTokenFromUrl } from './spotify';

function App() {
  useEffect(()=>{
    const hash = getTokenFromUrl();
    window.location.hash="";
    const token=hash.access_token;
    console.log('I HAVE A TOKEN on',token);
  },[]);
  return (
    <div className="app">
      <Login />
    </div>
  );
}

export default App;
